import React, { useEffect } from 'react';
import { addFavorite, deleteFavorite, getFavorites } from '../utils';
import Navbar from './Navbar';
import ""

const LandingPage = () => {
  useEffect(() => {
    const landingPageLogic = () => {
      const featuredFieldTogglers = document.querySelectorAll(".featured-fields__card-favorite");
      const fieldTogglers = document.querySelectorAll(".fields__card-favorite");
      const favorites = getFavorites();

      featuredFieldTogglers.forEach((toggler) => {
        const id = toggler.dataset.fieldId;

        var index = favorites.indexOf(id);
        if (index !== -1) {
          toggler.classList.add("featured-fields__card-favorite_active");
        } else {
          toggler.classList.remove("featured-fields__card-favorite_active");
        }

        toggler.addEventListener("click", () => {
          if (toggler.classList.contains("featured-fields__card-favorite_active")) {
            toggler.classList.remove("featured-fields__card-favorite_active");
            deleteFavorite(id);
            return;
          }

          toggler.classList.add("featured-fields__card-favorite_active");
          addFavorite(id);
        });
      });

      fieldTogglers.forEach((toggler) => {
        const id = toggler.dataset.fieldId;

        var index = favorites.indexOf(id);
        if (index !== -1) {
          toggler.classList.add("fields__card-favorite_active");
        } else {
          toggler.classList.remove("fields__card-favorite_active");
        }

        toggler.addEventListener("click", () => {
          if (toggler.classList.contains("fields__card-favorite_active")) {
            toggler.classList.remove("fields__card-favorite_active");
            deleteFavorite(id);
            return;
          }

          toggler.classList.add("fields__card-favorite_active");
          addFavorite(id);
        });
      });

      const filterForm = document.querySelector("form.filter");
      const button = filterForm.querySelector("button");
      const selects = filterForm.querySelectorAll("select");
      const dateInputs = filterForm.querySelectorAll("input[type='date']");

      selects.forEach((select) => {
        select.addEventListener("change", () => {
          button.click();
        });
      });

      dateInputs.forEach((input) => {
        input.addEventListener("input", () => {
          button.click();
        });
      });
    };

    window.addEventListener("load", landingPageLogic);

    return () => {
      window.removeEventListener("load", landingPageLogic);
    };
  }, []);

  return (
    <div>
      <Navbar />
      {/* Hero section */}
      <div className="container hero" data-animated>
        <div className="hero__left">
          <h1 className="hero__title">Rent our fields and conquer the game</h1>
          <p className="hero__subtitle">
            Elite Sports provides top-notch sports fields for rent, enabling you to elevate your game to new heights.
          </p>
          <a href="/fields" className="hero__button">Get started</a>
        </div>
        <div className="hero__right">
          <div className="hero__img-group">
            <img alt="Field" src="./img/heros/hero-1.png" />
            <img alt="Field" src="./img/heros/hero-2.png" />
          </div>
          <div className="hero__img-group">
            <img alt="Field" src="./img/heros/hero-3.png" />
            <img alt="Field" src="./img/heros/hero-4.png" />
          </div>
        </div>
      </div>

      {/* Filter form */}
      <form action="" className="container filter" data-animated>
        <div className="filter__search">
          <label htmlFor="search">
            <img alt="Search" src="./img/icons/magnifier-gray.png" />
          </label>
          <input
            id="search"
            name="search"
            type="search"
            placeholder="Search for fields"
          />
          <button type="submit">Search</button>
        </div>
        <div className="filter__list">
          <div className="filter__item">
            <label htmlFor="location">
              <img alt="Location" src="./img/icons/location-gray.png" />
            </label>
            <select id="location" name="location" defaultValue="">
              <option value="" disabled>Location</option>
              <option value="location-1">Location 1</option>
              <option value="location-2">Location 2</option>
              <option value="location-3">Location 3</option>
            </select>
          </div>
          <div className="filter__item">
            <label htmlFor="category">
              <img alt="Category" src="./img/icons/grid-8-gray.png" />
            </label>
            <select id="category" name="category" defaultValue="">
              <option value="" disabled>Category</option>
              <option value="category-1">Category 1</option>
              <option value="category-2">Category 2</option>
              <option value="category-3">Category 3</option>
            </select>
          </div>
          <div className="filter__item">
            <label htmlFor="time">
              <img alt="Time" src="./img/icons/clock-gray.png" />
            </label>
            <input id="time" name="time" type="date" />
          </div>
        </div>
      </form>

      {/* Featured fields section */}
      <div className="container featured-fields">
        <div className="featured-fields__header" data-animated>
          <h2>Featured fields</h2>
          <a href="/fields">Browse all fields</a>
        </div>
        <div className="featured-fields__body">
          <div className="featured-fields__card" data-animated>
            {/* Your card content here */}
          </div>
          <div className="featured-fields__card" data-animated>
            {/* Your card content here */}
          </div>
        </div>
      </div>

      {/* How to rent a field section */}
      <div className="container how">
        <h2 className="how__header" data-animated>
          How to rent a field in 3 easy steps
        </h2>
        <div className="how__body">
          {/* Your steps content here */}
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
