import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGooglePlusG, faFacebookF, faGithub, faLinkedinIn } from '@fortawesome/free-brands-svg-icons';

const LoginSignup = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your form submission logic here
  };

  const handleToggle = () => {
    setIsSignUp(prevState => !prevState);
  };

  return (
    <div style={{ fontFamily: 'Montserrat, sans-serif', backgroundColor: '#c9d6ff', background: 'linear-gradient(to right, #e2e2e2, #c9d6ff)', display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection: 'column', height: '100vh' }}>
      <div className={`container ${isSignUp ? 'active' : ''}`} style={{ backgroundColor: '#fff', borderRadius: '30px', boxShadow: '0 5px 15px rgba(0, 0, 0, 0.35)', position: 'relative', overflow: 'hidden', width: '768px', maxWidth: '100%', minHeight: '480px' }}>
        <div className="form-container sign-up" style={{ position: 'absolute', top: 0, left: 0, width: '50%', opacity: 0, zIndex: 1 }}>
          <form>
            <h1>Create Account</h1>
            <div className="social-icons">
              <a href="#" className="icon"><FontAwesomeIcon icon={faGooglePlusG} /></a>
              <a href="#" className="icon"><FontAwesomeIcon icon={faFacebookF} /></a>
              <a href="#" className="icon"><FontAwesomeIcon icon={faGithub} /></a>
              <a href="#" className="icon"><FontAwesomeIcon icon={faLinkedinIn} /></a>
            </div>
            <span>or use your email for registration</span>
            <input type="text" placeholder="Name" />
            <input type="email" placeholder="Email" />
            <input type="password" placeholder="Password" />
            <button>Sign Up</button>
          </form>
        </div>
        <div className="form-container sign-in" style={{ position: 'absolute', left: 0, width: '50%', zIndex: 2 }}>
          <form onSubmit={handleSubmit}>
            <h1>Sign In</h1>
            <div className="social-icons">
              <a href="#" className="icon"><FontAwesomeIcon icon={faGooglePlusG} /></a>
              <a href="#" className="icon"><FontAwesomeIcon icon={faFacebookF} /></a>
              <a href="#" className="icon"><FontAwesomeIcon icon={faGithub} /></a>
              <a href="#" className="icon"><FontAwesomeIcon icon={faLinkedinIn} /></a>
            </div>
            <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username" />
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" />
            <a href="#">Forget Your Password?</a>
            <button type="submit">Sign In</button>
          </form>
        </div>
        <div className="toggle-container" style={{ position: 'absolute', top: 0, left: '50%', width: '50%', height: '100%', overflow: 'hidden', transition: 'all 0.6s ease-in-out', borderRadius: '150px 0 0 100px', zIndex: 1000 }}>
          <div className="toggle" style={{ backgroundColor: '#512da8', height: '100%', background: 'linear-gradient(to right, #5c6bc0, #512da8)', color: '#fff', position: 'relative', left: '-100%', width: '200%', transform: 'translateX(0)', transition: 'all 0.6s ease-in-out' }}>
            <div className="toggle-panel" style={{ position: 'absolute', width: '50%', height: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection: 'column', padding: '0 30px', textAlign: 'center', top: 0, transform: 'translateX(-200%)', transition: 'all 0.6s ease-in-out' }}>
              <h1>Welcome Back!</h1>
              <p>Enter your personal details to use all site features</p>
              <button className={`${isSignUp ? '' : 'hidden'}`} onClick={handleToggle}>Sign In</button>
            </div>
            <div className="toggle-panel" style={{ position: 'absolute', width: '50%', height: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection: 'column', padding: '0 30px', textAlign: 'center', top: 0, right: 0, transform: 'translateX(0)', transition: 'all 0.6s ease-in-out' }}>
              <h1>Hello, Friend!</h1>
              <p>Register with your personal details to use all site features</p>
              <button className={`${isSignUp ? 'hidden' : ''}`} onClick={handleToggle}>Sign Up</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginSignup;
