import React, { useState } from 'react';

const LoginSignup = () => {
  const [isSignInActive, setIsSignInActive] = useState(true);

  const handleToggleForm = () => {
    setIsSignInActive(prevState => !prevState);
  };

  return (
    <div className={`wrapper ${isSignInActive ? '' : 'active'}`}>
      <style>
        {`
          @import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600;700;800;900&display=swap');
          * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            font-family: 'Poppins', sans-serif;
          }
          body {
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
            background: #000;
          }
          .wrapper {
            position: relative;
            width: 400px;
            height: 500px;
            background: #000;
            box-shadow: 0 0 50px #0ef;
            border-radius: 20px;
            padding: 40px;
            overflow: hidden;
          }
          .wrapper:hover {
            animation: animate 1s linear infinite;
          }
          @keyframes animate {
            100% {
              filter: hue-rotate(360deg);
            }
          }
          .form-wrapper {
            display: flex;
            justify-content: center;
            align-items: center;
            width: 100%;
            height: 100%;
            transition: 1s ease-in-out;
          }
          .wrapper.active .form-wrapper.sign-in {
            transform: translateY(-450px);
          }
          .wrapper .form-wrapper.sign-up {
            position: absolute;
            top: 450px;
            left: 0;
          }
          .wrapper.active .form-wrapper.sign-up {
            transform: translateY(-450px);
          }
          h2 {
            font-size: 30px;
            color: #fff;
            text-align: center;
          }
          .input-group {
            position: relative;
            margin: 30px 0;
            border-bottom: 2px solid #fff;
          }
          .input-group label {
            position: absolute;
            top: 50%;
            left: 5px;
            transform: translateY(-50%);
            font-size: 16px;
            color: #fff;
            pointer-events: none;
            transition: .5s;
          }
          .input-group input {
            width: 320px;
            height: 40px;
            font-size: 16px;
            color: #fff;
            padding: 0 5px;
            background: transparent;
            border: none;
            outline: none;
          }
          .input-group input:focus~label,
          .input-group input:valid~label {
            top: -5px;
          }
          .remember {
            margin: -5px 0 15px 5px;
          }
          .remember label {
            color: #fff;
            font-size: 14px;
          }
          .remember label input {
            accent-color: #0ef;
          }
          button {
            position: relative;
            width: 100%;
            height: 40px;
            background: #0ef;
            box-shadow: 0 0 10px #0ef;
            font-size: 16px;
            color: #000;
            font-weight: 500;
            cursor: pointer;
            border-radius: 30px;
            border: none;
            outline: none;
          }
          .signUp-link {
            font-size: 14px;
            text-align: center;
            margin: 15px 0;
          }
          .signUp-link p {
            color: #fff;
          }
          .signUp-link p a {
            color: #0ef;
            text-decoration: none;
            font-weight: 500;
          }
          .signUp-link p a:hover {
            text-decoration: underline;
          }
        `}
      </style>
      <div className={`form-wrapper sign-in ${isSignInActive ? 'active' : ''}`}>
        <form action="">
          <h2>Login</h2>
          <div className="input-group">
            <input type="text" required />
            <label>Username</label>
          </div>
          <div className="input-group">
            <input type="password" required />
            <label>Password</label>
          </div>
          <div className="remember">
            <label><input type="checkbox" /> Remember me</label>
          </div>
          <button type="submit">Login</button>
          <div className="signUp-link">
            <p>Don't have an account? <span onClick={handleToggleForm}>Sign Up</span></p>
          </div>
        </form>
      </div>
      <div className={`form-wrapper sign-up ${isSignInActive ? '' : 'active'}`}>
        <form action="">
          <h2>Sign Up</h2>
          <div className="input-group">
            <input type="text" required />
            <label>Username</label>
          </div>
          <div className="input-group">
            <input type="email" required />
            <label>Email</label>
          </div>
          <div className="input-group">
            <input type="password" required />
            <label>Password</label>
          </div>
          <div className="remember">
            <label><input type="checkbox" /> I agree to the terms & conditions</label>
          </div>
          <button type="submit">Sign Up</button>
          <div className="signUp-link">
            <p>Already have an account? <span onClick={handleToggleForm}>Sign In</span></p>
          </div>
        </form>
      </div>
    </div>

  );
};

export default LoginSignup;
